from functools import reduce
str1=""
kill = False
multlist=[]
biggest = 0
a = 0
b = 13
#print(str1[a:b])
while True:
    multlist = []
    for x in str1[a:b]:

        multlist.append(int(x))
        product = reduce(lambda x, y: x*y, multlist)

        if biggest < product: biggest = product
        if b == 999: kill = True
    a += 1
    b += 1
    if kill == True:
        break
